    Document



     {{1.98 + 2.98 | number:1 | json }}


        {{ (x.name | uppercase) + ', ' + x.country }}



    var app = angular.module("myApp", []);
    app.controller('life', function($scope){
        $scope.search = '222';


        $scope.now = [{name:'Jani',country:'Norway'},
        {name:'Hege',country:'Sweden'},
        {name:'Kai',country:'Denmark'}]
    })



